package fr.vendredi.repository;

import javax.inject.Inject;
import javax.inject.Named;
import javax.inject.Singleton;
import fr.vendredi.domain.Account;
import fr.vendredi.domain.Document;
import fr.vendredi.repository.AccountGenerator;
import fr.vendredi.repository.AccountRepository;

/**
 * Helper class to create transient entities instance for testing purposes.
 * Simple properties are pre-filled with random values.
 */
@Named
@Singleton
public class DocumentGenerator {

    /**
     * Returns a new Document instance filled with random values.
     */
    public Document getDocument() {
        Document document = new Document();

        // simple attributes follows
        document.setDocumentBinary("dummy".getBytes());
        document.setDocumentFileName("dummy.txt");
        document.setDocumentContentType("application/text");
        document.setDocumentSize(1);
        // mandatory relation
        Account owner = accountGenerator.getAccount();
        accountRepository.save(owner);
        document.setOwner(owner);
        return document;
    }

    @Inject
    private AccountRepository accountRepository;
    @Inject
    private AccountGenerator accountGenerator;
}