package fr.vendredi.web.selenium.page.savedsearch;

import static org.openqa.selenium.By.name;

import org.openqa.selenium.WebElement;
import org.openqa.selenium.support.FindBy;

import fr.vendredi.web.selenium.support.Page;
import fr.vendredi.web.selenium.page.AbstractSearchPage;

@Page
public class SavedSearchSearchPage extends AbstractSearchPage {
    // search box
    @FindBy(id = "form:name_input")
    public WebElement name;
    @FindBy(id = "form:formClassname_input")
    public WebElement formClassname;
    @FindBy(id = "form:accountSelector_input")
    public WebElement account;

    public void edit(String savedSearch) {
        clickEdit(savedSearch);
    }

    public void add(String savedSearch) {
        clickAdd(savedSearch);
    }

    public void delete(String savedSearch) {
        clickDelete(savedSearch);
        webClient.click(name("form:askForDeleteItemDialogYes"));
    }

    public void select(String savedSearch) {
        clickSelect(savedSearch);
    }
}